mapper.select:
SELECT 
FROM activity_participant
WHERE activity_id = #{activityId} AND user_id = #{userId}

mapper.update:
UPDATE activity_participant
SET status = #{status}
WHERE activity_id = #{activityId} AND user_id = #{userId}

UPDATE activity_participant
SET certificate_url = #{certificateUrl}
WHERE activity_id = #{activityId} AND user_id = #{userId}

mapper.select:
SELECT COUNT(*) 
FROM activity_participant
WHERE activity_id = #{activityId}
